mapper.select:
select

from cache_rule


        and MOCK_RULE_ID = #{mockRuleId}


        and CACHE_OPTION = #{cacheOption}


        and REQ_CACHE_RULE = #{reqCacheRule}


        and CACHE_BODY = #{cacheBody}


        and CACHE_BODY_MATCH_RULE = #{cacheBodyMatchRule}


        and CACHE_TIME = #{cacheTime}


        and NULL_ResponsE_TEMPLATE = #{nullResponseTemplate}


        and RESPONSE_TEMPLATE = #{responseTemplate}


        and MATCH_ERROR_RESPONSE_TEMPLATE = #{matchErrorResponseTemplate}


        and MATCH_STATUS_CODE = #{matchStatusCode}


        and NULL_MATCH_STATUS_CODE = #{nullMatchStatusCode}


        and MATCH_ERROR_STATUS_CODE = #{matchErrorStatusCode}


        and CREATED = #{created}


        and UPDATED = #{updated}


        and REMARK = #{remark}



    limit #{startPosition},#{endPosition}

select count(1)
from cache_rule


        and MOCK_RULE_ID = #{mockRuleId}


        and CACHE_OPTION = #{cacheOption}


        and REQ_CACHE_RULE = #{reqCacheRule}


        and CACHE_BODY = #{cacheBody}


        and CACHE_BODY_MATCH_RULE = #{cacheBodyMatchRule}


        and CACHE_TIME = #{cacheTime}


        and NULL_ResponsE_TEMPLATE = #{nullResponseTemplate}


        and RESPONSE_TEMPLATE = #{responseTemplate}


        and MATCH_ERROR_RESPONSE_TEMPLATE = #{matchErrorResponseTemplate}


        and MATCH_STATUS_CODE = #{matchStatusCode}


        and NULL_MATCH_STATUS_CODE = #{nullMatchStatusCode}


        and MATCH_ERROR_STATUS_CODE = #{matchErrorStatusCode}


        and CREATED = #{created}


        and UPDATED = #{updated}


        and REMARK = #{remark}
